         <script>
            $(function() {
            
                IS_COMPANY = 1
                    
                $('input[name="probabilite"]').on('focusout', function() {
                    if(parseInt($(this).val()) > 100 || !($.isNumeric($(this).val()))) {
                        $(this).val('')
                    }
                })
                TOKEN = undefined
                    $.ajax(
                        {
                            url: '/ksm/admin/customers/getcsrf',
                            success: function(data){
                                    data = JSON.parse(data)
                                    TOKEN = data.csrfHash
                            }
                        }
                    )
                
                    $("#addProspect .btn-success").on('click', function($e) {
            
                        //AJAX SETUP
                        $.ajaxSetup({
                            headers: {
                            'X-CSRF-TOKEN': $('meta[name="csrf-token"]').attr('content')
                            }
                        });
                        $e.preventDefault()
                     
                        iS_INVALID = false
                        let company_name = $('select[name="entreprise-name"]').val()
                        let fullname = $('input[name="lead-name"]').val()
                        let arr_name =  fullname?fullname.split(" ", 2):['', ''];
                        let route1 = window.origin + "/ksm/admin/customers/add"
                        arr_required = ['input[name="intitule"]', '#lead_data', '#date_start', '#date_end', '#sujet_data']
                       
                           //si l'utilisateur crée un nouveau prospect
                       
                         if($(".create-prospect").is(':visible')) {
                             arr_required[1] = "input[name='lead-name']"
                             if(IS_COMPANY == 1) {
                              arr_required.push("select[name='entreprise-name']")
                             } else {
                                 company_name = ''
                             }
                         }
                        for(let i of arr_required) {
                            if($(i).val() == '' || $(i).val() == null) {
                                if(i == "select[name='entreprise-name']") {
                                    $('#modal-container .select2-container--default .select2-selection--single').addClass('is-invalid')
                                } else {
                                    $(i).addClass('is-invalid')
                                }
                                 iS_INVALID = true
                            } else {
                                if(i == "select[name='entreprise-name']") {
                                    $('#modal-container .select2-container--default .select2-selection--single').removeClass('is-invalid')
                                } else {
                                    $(i).removeClass('is-invalid')
                                }
                            }
                        }
                     
                        if(iS_INVALID) return
                       
                            let data1 = {
                                    token: TOKEN,
                                    company: company_name,
                                    vat_no: '',
                                    siren_data: '', 
                                    name: fullname,
                                    customer_group: 1,
                                    price_group: 1,
                                    group_name: 'lead',
                                    email: '',
                                    note_data: '...',
                                    phone: '',
                                    address: '',
                                    contact_person: fullname,
                                    employee_data: $('#liste_users').val(),
                                    is_company: IS_COMPANY,
                                    numero_tva: '',
                                    site_web: '',
                                    assujetissement: '',
                                    city: '',
                                    postal_code: '',
                                    langue: '',
                                    condition_de_paiement: '',
                                    numero_IBAN: '',
                                    code_BIC: '',
                                    type_entreprise: '',
                                    devise: '',
                                    contact: '',
                                    numero_fixe: '',
                                    mobile_number: '',
                                    designation: '',
                                    date: moment().format('Y-MM-DD'),
                                    add_customer: 'Validate'
                            }
            
                         
                        var date_start = document.getElementById('date_start').value;
                        var date_end =   document.getElementById('date_end').value
                        var prospect = document.getElementById('lead_data').value;
                        var sujet = document.getElementById('sujet_data').value;
                        var note = document.getElementById('note_data').value;
                        var created_by = "66";
            
            
                        if (date_start == '') {
                            $("#date_start_").addClass('is-invalid');
                        }
                        else if(date_end == '') {
                            $("#date_end").addClass('is-invalid');
                            $(".warning_date_on_create").removeClass('hidden');
                        } else {
                             $("#date_end, #date_start").removeClass('is-invalid');
                             $('.btn.btn-success').attr('disabled', true)
            
                          let route = window.origin +'/kcm-remake/public/index.php/kanban_leads/contact'
                          date_start = moment(date_start, "DD/MM/YYYY HH:mm").format('YYYY-MM-DD HH:mm:ss')
                          date_end =  moment(date_end, "DD/MM/YYYY HH:mm").format('YYYY-MM-DD HH:mm:ss')
                          v_created_by =  created_by
                          if((null == $('#liste_users').val() && "0" == 1) || "0" == 0) {
                                v_created_by = created_by
                            } else {
                                v_created_by = $('#liste_users').val()
                            }
                           let data = {
                                    intitule: $("input[name='intitule']").val(),
                                    date_start: date_start,
                                    date_end: date_end,
                                    montant: $('input[name="montant"]').val(),
                                    prospect: prospect,
                                    sujet: sujet, //le produit
                                    note: note,
                                    probabilite:  $('input[name="probabilite"]').val(),
                                    source: $('#lead_source').val(), //le responsable
                                    created_by: v_created_by
                            }
            
                            //Verfifier si le user ne crée pas un nouveau prospect
                        if($(".create-prospect").is(':visible')) {
                             submitForm(data1, route1).done(res=> {
                               
                                 let response = JSON.parse(res)
                                 data.prospect =  response.id_customer
                                 create_Kanban_lead(data, route)
                             })
                           return
                        }
                            create_Kanban_lead(data, route)
            
                            
                        }
                    })
                    
                    let create_Kanban_lead = (data, route)=> {
                        submitForm(data, route).done(result=> {
                                   
                                    $('.btn.btn-success').attr('disabled', false)
                                    $('#to-do').prepend(result)
                                    $("#addProspect").modal('hide')
                                    document.getElementById('form-add-o').reset()
                            })
                    }
            
                    // Soumission du formulaire d'ajout d'opportunité
                    let submitForm =  (data, route)=> {
                         $('.save-opp').attr('disabled', true)
                       return $.ajax({
                                method: "POST",
                                url: route, //ajout dans la table kanban_leads de kcm
                                data: {...data}
                            }).fail(err=> {
                                console.error(err)
                                    $('.save-opp').attr('disabled', false)
                                     $('.btn.btn-success').attr('disabled', false)
                                     alert("une erreur s'est passé lors de la validation !")
                            })
                    }
            
                    var modaladdProspect = document.getElementById('addProspect')
            
                    modaladdProspect.addEventListener('show.bs.modal', function (event) {
                        $('#addProspect').addClass('d-flex justify-content-center align-items-center')
                    })
                    modaladdProspect.addEventListener('hidden.bs.modal', function (event) {
                        $('#addProspect').removeClass('d-flex justify-content-center align-items-center')
                        $('.list-prospect').show()
                        $('.create-prospect').hide()
                        for(let i of arr_required) {
                            if(i == "select[name='entreprise-name']") {
                                    $('#modal-container .select2-container--default .select2-selection--single').removeClass('is-invalid')
                                } else {
                                    $(i).removeClass('is-invalid')
                                }
                        }
                    });
            
                function formatState (state) {
                              
                            if(!state.id || state?.l1_normalisee == "UNDEFINED FUNCTION" || state?.l1_normalisee == "UNDEFINED") return
                            
                          
                            var $state = $(
                                `<span> ${state?.l1_normalisee} </span> <small style='color: "#6c757d"'> - ${state.geo_adresse??''} </small>`
                            );
                            return $state;
                        };
            
                    function formatRepoSelection(repo) {
                        
                            var $state = $(
                                `<div> ${repo?.l1_normalisee} </div>`
                            );
                        return $state;
                    }
            
            let opt = {
                dropdownParent: "#modal-container",
                ajax: {
                    url: function(params) {
                        if(!params.term) return
                    return `https://entreprise.data.gouv.fr/api/sirene/v1/full_text/${params.term}`
                    },
                    data: { search: true},
                    dataType: 'json',
                    processResults: function (data) {
                      
                        // Transforms the top-level key of the response object from 'items' to 'results'
                        return {
                            results: data.etablissement
                        };
                    },
                },
            cache: true,
            templateResult: formatState,
            templateSelection: formatRepoSelection,
            language: {
                noResults: function() {
                    return "Aucun resultat trouvé"
                },
                errorLoading:function(){
                    return"Les résultats ne peuvent pas être chargés."
                    },
                searching: function(){
                    return"Recherche en cours…"
                    }
            },
            }
            $('#_entreprise_data_').select2(opt).on('select2:select', function() {
            
            $('#_entreprise_data_ option').val($('.select2-selection__rendered div').text().trim());
            
            }).on('select2:open', function() {
            
            });
            
            $('.AjoutProspect').on('click', function() {
                        $('.list-prospect').hide()
                        $('.create-prospect').show()
                    });
                
            
            addTask = ()=> {
                    $('#addProspect').modal('show');
                }
            
                $('.modal-add-lead .border-secondary, .modal-add-lead .close-opp').on('click',()=> {
                            $('.modal-add-lead').hide()
                        });
                let optional_config = {
                            'locale': 'fr',
                            time_24hr: true
                        }
            
                $("#date_start, #date_end").flatpickr(
                            {
                                ...optional_config,
                                enableTime: true,
                                dateFormat: "d/m/Y H:i",
                                position: 'above right'
                            }
                        )
            
            });
         </script>
