         <script>
            $('.submitPlanification').on('click', function($e) {
            
                    //Validateurs de planification d'evenements
                    let arr_required = ['select[name="type_action"]', 'input[name="date_p"]', 'input[name="date_debut_p"]', 'input[name="date_fin_p"]', 'select[name="intitule_p"]', 'textarea[name="description"]']
                    iS_INVALID = false
                    for(let i of arr_required) {
                                    if($(i).val() == '' || $(i).val() == null) {
                                        console.log("coupable ", i)
                                        $(i).addClass('danger')
                                        iS_INVALID = true
                                    } else {
                                        if($(i).hasClass('danger')) {
                                            $(i).removeClass('danger')
                                        }
                                    }
                                }
                    let participants = $('.participants_internes').val()
                    console.log('*** ', participants.length)
                    if(participants.length <= 0) {
                        $('.select2-selection.select2-selection--multiple').addClass('danger')
                    iS_INVALID = true
                    } else {
                        $('.select2-selection.select2-selection--multiple').removeClass('danger')
                    }
            
                    if(iS_INVALID) return
            
            
                    $('.submitPlanification').prop('disabled', true)
                    let text = null
                    for(let part of participants) {
                        text += `,member:${part}`
                    }
                    let date = ($('input[name="date_p"]').val())
            
                    let participants_internes = null !==text?text.replace('null,',''):null
                    let type = $('select[name="type_action"]').val();
                    let heure_debut = ($('input[name="date_debut_p"]').val());
                    let heure_fin = ($('input[name="date_fin_p"]').val());
                    let intitule = $('input[name="intutile"]').val();
                    let title = $('select[name="intitule_p"]').val();
                    let description = $('textarea[name="description"]').val();
                    let id_opportunity  = $('select[name="intitule_p"] :selected').data('id-type');
                    let r_type = $('select[name="intitule_p"] :selected').data('type');
                    let last_step = $('select[name="intitule_p"] :selected').data('last-step');
                    let raw_text = $('select[name="type_action"] :selected').text().trim('');
                  
                    //$('#date_end_n').val().replace(' AM', '').replace(' PM', '')
            
                    $.ajax(
                        {
                            type: 'POST',
                            url: location.origin + '/kcm-remake/public/add_event',
                            data: {intitule: title, type: type, r_type: r_type, participants_internes: participants_internes, heure_debut: heure_debut, heure_fin: heure_fin, date: date, id_opportunity: id_opportunity, description: description, last_step: last_step, note: raw_text},
                            success: res=> {
                                $('.modal-fade-planifier').css('display', 'none');
                                location.href = location.href
                            },
                            error: err=> {
                                alert("L'action a echoué !")
                                
                            },
                            always:()=> {
                                $('.submitPlanification').prop('disabled', false)
                            }
                        }
                    )
                });
            
            
         </script>
