                               ContactIds.push(numberOfContact)
                               numberOfContact++;
                           })
                           
                           $(document).on("click",'.deleteContact',function () {
                               $(this).attr("id")
                               $(this).parent().hide()
                               var i=0 ;
                               while (i<ContactIds.length) {
                                   if ($(this).attr("id") ==ContactIds[i]) {
                                       ContactIds.splice(i,1)
