                           }
                           
                           const AllContactsValid=()=>{
                           let valide=true;
                           if(IS_COMPANY)
                           ContactIds.forEach(id => {
                               $(`#nom_${id}`).val() ==''?$(`#nom_${id}`).addClass('is-invalid'):$(`#nom_${id}`).removeClass('is-invalid')
                               $(`#prenom_${id}`).val() ==''?$(`#prenom_${id}`).addClass('is-invalid'):$(`#prenom_${id}`).removeClass('is-invalid')
                               $(`#email_${id}`).val() !==''?checkEmail($(`#email_${id}`).val())?$(`#email_${id}`).removeClass('is-invalid'):$(`#email_${id}`).addClass('is-invalid'):""
                               $(`#poste_${id}`).val() ==''?$(`#poste_${id}`).addClass('is-invalid'):$(`#poste_${id}`).removeClass('is-invalid')
                               
                               if($(`#phone_${id}`).val() !=='')
                                   if ($(`#phone_${id}`).val() !=='' && (($(`#phone_${id}`).val()).length >= 1 && ($.isNumeric($(`#phone_${id}`).val())) && ($(`#phone_${id}`).val().length < 10 || $(`#phone_${id}`).val().length > 10) || $(`#phone_${id}`).val().indexOf('+') == 0) || !$.isNumeric($(`#phone_${id}`).val()) && $(`#phone_${id}`).val() && $(`#phone_${id}`).val().length >= 1) {
                                       $(`#phone_${id}`).addClass('is-invalid')
                                   } else {
                                       $(`#phone_${id}`).removeClass('is-invalid')
                                       
                                   }
                               if( $(`#phone_${id}`).hasClass('is-invalid') || $(`#nom_${id}`).hasClass('is-invalid') || $(`#prenom_${id}`).hasClass('is-invalid') || $(`#phone_${id}`).hasClass('is-invalid') || $(`#email_${id}`).hasClass('is-invalid')){
                                   valide=false
                               }
                           });
                           else
                               return true;
                           
                           return valide;
                           }
                           function checkEmail(email) {
                           var re = /^(([^<>()[\]\\.,;:\s@\"]+(\.[^<>()[\]\\.,;:\s@\"]+)*)|(\".+\"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
                           return re.test(email);
                           }
                           
                           function formatState (state) {
                               console.log('state == ', state)
                           if(!state.id || state?.l1_normalisee == "UNDEFINED FUNCTION" || state?.l1_normalisee == "UNDEFINED") return
                           
                           console.log('state ++ ', state)
                           var $state = $(
                               `<span> ${state?.l1_normalisee} </span> <small style='color: "#6c757d"'> - ${state.geo_adresse??''} </small>`
                           );
                           return $state;
                           };
                           
                           function formatRepoSelection(repo) {
                               $('#siren_data').val(repo.siret)
                               console.log('response ', repo)
                                   var $state = $(
                                       `<span> ${repo?.l1_normalisee} </span> <small style='color: "#6c757d"'> - ${repo.geo_adresse??''} </small>`
                                   );
                               return $state;
                           }
                           
                           let opt = {
                               dropdownParent:"#ent_data",
                               ajax: {
                                   url: function(params) {
                                       if(!params.term) return
                                     return `https://entreprise.data.gouv.fr/api/sirene/v1/full_text/${params.term}`
                                   },
                                   dataType: 'json',
                                   processResults: function (data) {
                                       console.log('data ++ ', data.etablissement)
                                       // Transforms the top-level key of the response object from 'items' to 'results'
                                       return {
                                           results: data.etablissement
                                       };
                                   },
                               },
                               cache: true,
                               templateResult: formatState,
                               templateSelection: formatRepoSelection,
                               language: {
                                   noResults: function() {
                                       return "Aucun resultat trouvé"
                                   },
                                   errorLoading:function(){
                                       return"Les résultats ne peuvent pas être chargés."
                                       },
                                   searching: function(){
                                       return"Recherche en cours…"
                                       }
                               },
                           }
                           $('#entreprise_data').select2(opt).on('select2:select', function() {
                                   $('#entreprise_data option').val($('.select2-selection__rendered span').text().trim())
                           });
                           
                           
