                                    if(tab) {
                                        location.href = location.origin + location.pathname +"?history="+tab
                                    }
                        
                                });
                        
                                const switchIcone = (q, toRemove, toReplace)=> {
                        
                                    $('.voirNote-parent i').removeClass('bi-dash').addClass('bi-plus');
                        
                                    let section = $(".detail section");
                        
                        
                                    let el =  $(q).find('.voirNote-parent i');
                        
                                    section.removeClass('active').find('.branch').addClass('d-none');
                        
                                    console.log(' toRemove ', toRemove, ' ** ', toReplace)
                        
                                    if(el.hasClass(toRemove)) {
                                        el.removeClass(toRemove);
                                        el.addClass(toReplace);
                                    }
                        
                                    if(toReplace == 'bi-dash') {
                                        el.parents('section').addClass('active')
                                    }
                        
                                     $(".detail section.active").find('.branch').removeClass('d-none');
                        
                                }
                        
                                $(".detail section:first-child").addClass('active');
                        
                                switchIcone("section.active", "bi-plus", "bi-dash");
                        
                                $('.voirNote-parent').on('click', function() {
                        
                                    let toRemove = 'bi-dash';
                                    let toReplace = 'bi-plus';
                                    let section  = $(this).parents('section');
                                    
                        
                                    if($(this).find('i').hasClass('bi-plus')) {
                                        toRemove = 'bi-plus';
                                        toReplace = 'bi-dash';
                                    }
                                    switchIcone(section, toRemove, toReplace);
                                });
                        });
