                        const AllContactsValid=()=>{
                        let valide=true;
                        if(IS_COMPANY)
                        ContactIds.forEach(id => {
                            $(`#nom_${id}`).val() ==''?$(`#nom_${id}`).addClass('is-invalid'):$(`#nom_${id}`).removeClass('is-invalid')
                            $(`#prenom_${id}`).val() ==''?$(`#prenom_${id}`).addClass('is-invalid'):$(`#prenom_${id}`).removeClass('is-invalid')
                            $(`#email_${id}`).val() !==''?checkEmail($(`#email_${id}`).val())?$(`#email_${id}`).removeClass('is-invalid'):$(`#email_${id}`).addClass('is-invalid'):""
                            $(`#poste_${id}`).val() ==''?$(`#poste_${id}`).addClass('is-invalid'):$(`#poste_${id}`).removeClass('is-invalid')
                            
                            if($(`#phone_${id}`).val() !=='')
                                if ($(`#phone_${id}`).val() !=='' && (($(`#phone_${id}`).val()).length >= 1 && ($.isNumeric($(`#phone_${id}`).val())) && ($(`#phone_${id}`).val().length < 10 || $(`#phone_${id}`).val().length > 10) || $(`#phone_${id}`).val().indexOf('+') == 0) || !$.isNumeric($(`#phone_${id}`).val()) && $(`#phone_${id}`).val() && $(`#phone_${id}`).val().length >= 1) {
                                    $(`#phone_${id}`).addClass('is-invalid')
                                } else {
