                            if( $(`#phone_${id}`).hasClass('is-invalid') || $(`#nom_${id}`).hasClass('is-invalid') || $(`#prenom_${id}`).hasClass('is-invalid') || $(`#phone_${id}`).hasClass('is-invalid') || $(`#email_${id}`).hasClass('is-invalid')){
                                valide=false
                            }
                        });
                        else
                            return true;
                        
                        return valide;
                        }
                        function checkEmail(email) {
                        var re = /^(([^<>()[\]\\.,;:\s@\"]+(\.[^<>()[\]\\.,;:\s@\"]+)*)|(\".+\"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
                        return re.test(email);
                        }
                        
                        function formatState (state) {
