         //Visualiser le fichier
         $('.table-file, .bi.bi-eye').on('click', function() {
                 
                 const path = $(this).data('path');
                 const name = $(this).data('name');
                 
                 console.log('path ', path, ' name ', name)
         
         
                 const fichier = name;
                 extension = fichier.split('.').pop();
                 var src='../../../storage/app/'+ path;
                 var doc = '../../../storage/app/'+ path;
                 var url = 'https://'+window.location.host;
         
                
                 
                     $(".viewFile .view-body").html("");
                 if (extension == "docx" || extension == "doc" || extension == "pptx" || extension == "ppt" || extension == "xlsx" || extension == "xls"){
                    
                         $(".viewFile .view-body").html('<iframe id="i_frame" style="width:100%;height:93vh;" src="https://view.officeapps.live.com/op/embed.aspx?src='+url+'/kcm-remake/storage/app/'+path+'&embedded=true" loading="lazy"></iframe>');
                     } else if (extension == "txt" || extension == "pdf" || extension == "sql" || extension == "xls"){
                     $(".view-body").html('<div><iframe src='+src+' style="width:100%; min-height:1100px;"></iframe></div>');
                 }
                else if(extension == "mp4"){
                     $(".viewFile .view-body").html('<video width="100%" height="100%" controls autoplay><source src='+src+' type="video/mp4"></video>');
                 }
                else if(extension == "mp3"){
                     $(".viewFile .view-body").html('<div><audio style="margin: 0 auto; display: block;" controls autoplay><source src='+src+' type="audio/mp3"></audio></div>');
                 }
                 else if(extension == "jpg" || extension == "JPG" || extension == "svg" || extension == "SVG" || extension == "jpeg" || extension == "JPEG"  || extension == "png" || extension == "PNG"){
                     $(".viewFile .view-body").html('<img src='+src+' style="width:100%; height:auto"></img>');
                 } else {
                     $(".viewFile .view-body").html('<h3 style="font-size: 12px;margin: 0;padding: 1rem;"> Ce Format de fichier ne peut être visualisé. Essayez de le télécharger </h3>');
                 }
                     $(".viewFile").css("display","block");
               });
         
               //Fonction pour supprimer un fichier
                 $('.delete-file').on('click', function() {
                     let id = parseInt($(this).data('id'))
                     $('.col-del-file').css('display', 'block')
                     $('.col-del-file .border-bottom.border-top .p-3').text('Voulez-vous supprimer ce fichier ?');
                         $('.col-del-file .border-secondary').on('click',()=> {
                                 $('.col-del-file').css('display', 'none')
                         } )
                     $('.col-del-file .btn-success').on('click',()=> {
                         $.ajax({
                             url: `../../files/${id}` ,
                             type: 'DELETE',
                             success: (res)=> {
                                 console.log('suppression faite ', res)
                                 window.location.reload()
                                 $('.col-del-file').css('display', 'none')
                             },
                             error: (err)=> {
                                 console.error('une erreur est arrivée ', err)
                             }
                         })
                    })
                 })
         
