                        let numberOfContact=0;
                        $('.col-choice').on('click', function() {
                            if($(this).find('.circle-choice').hasClass("active")){
                            }else{
                                let label = $(this).find('label').attr('for')
                                if(label == 'titre2') {
                                    $('.professionnels').hide()
                                    $('.Particuliers').show()
                                    IS_COMPANY = 0
                                    IS_CUSTOMER='lead'
                                }else {
                                    $('.professionnels').show()
                                    $('.Particuliers').hide()
                                    IS_COMPANY = 1
